import math
import gc
import logging

class Vector():
  def __init__(self, x, y):
    self.x = x
    self.y = y

class Rect():
  def __init__(self, x, y, w, h):
    self.x = x
    self.y = y
    self.w = w
    self.h = h

class Celeste():
  def __init__(self, pico8):
    global p8, g
    p8, g = pico8, self

    # game globals
    self.room = Vector(0, 0)
    self.objects = []
    self.freeze = 0
    self.delay_restart = 0
    self.next_rm = False # [change] decouple next room from object loop

    self.max_djump = 1

    self.k_left = 0
    self.k_right = 1
    self.k_up = 2
    self.k_down = 3
    self.k_jump = 4
    self.k_dash = 5

    self.tiles = {
      1: self.player_spawn,
      8: self.key,
      11: self.platform,
      12: self.platform,
      18: self.spring,
      20: self.chest,
      22: self.balloon,
      23: self.fall_floor,
      26: self.fruit,
      28: self.fly_fruit,
      64: self.fake_wall,
      #86: self.message,
      #96: self.big_chest,
      #118: self.flag
    }

  # entry point

  def _init(self):
    self.frames = 0
    self.load_room(0, 0)

  # main update loop

  def _update(self):
    self.frames = (self.frames + 1) % 30

    if self.freeze > 0:
      self.freeze -= 1
      return

    if self.delay_restart > 0:
      self.delay_restart -= 1
      if self.delay_restart == 0:
        self.load_room(self.room.x, self.room.y)

    for o in self.objects:
      o.move(o.spd.x, o.spd.y)
      if callable(getattr(o, 'update', None)):
        o.update()

    # [change] decouple next room from object loop
    if self.next_rm:
      self.next_rm = False
      lvl_id = self.level_index()
      n_objs = len(self.objects)
      self.load_room(lvl_id % 8, math.floor(lvl_id / 8))
      # simulate loading jank
      if lvl_id > 0:
        for o in self.objects[n_objs - 1:]:
          o.move(o.spd.x, o.spd.y)
          if callable(getattr(o, 'update', None)):
            o.update()
    
    # [change] remove destroyed objects after update calls
    while self.objects.count(None) > 0: self.objects.remove(None)

  # main draw loop (not actually for drawing)

  def _draw(self):
    if self.freeze > 0:
      return

    for o in self.objects:
      if callable(getattr(o, 'draw', None)):
        o.draw()

  # room stuff

  def level_index(self):
    return self.room.x + self.room.y * 8

  def restart_room(self):
    self.delay_restart=15

  def next_room(self, loop=False):
    # [change] decouple next room from object loop
    self.room = Vector((self.level_index() + 1) % 8, math.floor((self.level_index() + 1) / 8))
    self.next_rm = True

  def load_room(self, x, y):
    self.has_dashed = False
    self.has_key = False
    self.objects = []
    self.room.x = x
    self.room.y = y
    for tx in range(16):
      for ty in range(16):
        tile = p8.mget(self.room.x * 16 + tx, self.room.y * 16 + ty)
        if tile in self.tiles:
          self.init_object(self.tiles[tile], tx * 8, ty * 8, tile)

  # object base class

  class base_obj():
    def __init__(self, x, y, tile=None):
      self.collideable = True,
      self.solids = False
      self.spr = tile
      self.flip = Vector(False, False)
      self.x = x
      self.y = y
      self.hitbox = Rect(0, 0, 8, 8)
      self.spd = Vector(0.0, 0.0)
      self.rem = Vector(0.0, 0.0)

    def is_solid(self, ox, oy):
      if oy > 0 and not self.check(g.platform, ox, 0) and self.check(g.platform, ox, oy):
        return True
      return g.tile_flag_at(self.x + self.hitbox.x + ox, self.y + self.hitbox.y + oy, self.hitbox.w, self.hitbox.h, 0)\
        or self.check(g.fall_floor, ox, oy)\
        or self.check(g.fake_wall, ox, oy)

    def is_ice(self, ox, oy):
      return g.tile_flag_at(self.x + self.hitbox.x + ox, self.y + self.hitbox.y + oy, self.hitbox.w, self.hitbox.h, 4)

    def check(self, obj, ox, oy):
      for other in g.objects:
        if type(other) == obj and other != self and other.collideable and \
         other.x + other.hitbox.x + other.hitbox.w > self.x + self.hitbox.x + ox and \
         other.y + other.hitbox.y + other.hitbox.h > self.y + self.hitbox.y + oy and \
         other.x + other.hitbox.x < self.x + self.hitbox.x + self.hitbox.w + ox and \
         other.y + other.hitbox.y < self.y + self.hitbox.y + self.hitbox.h + oy:
          return other
      return None

    def move(self, ox, oy):
      self.rem.x += ox
      amt = math.floor(self.rem.x + 0.5)
      self.rem.x -= amt
      self.move_x(amt, 0)
      self.rem.y += oy
      amt = math.floor(self.rem.y + 0.5)
      self.rem.y -= amt
      self.move_y(amt)

    def move_x(self, amt, start):
      if self.solids:
        step = g.sign(amt)
        for i in range(start, abs(amt) + 1):
          if not self.is_solid(step, 0):
            self.x += step
          else:
            self.spd.x = 0
            self.rem.x = 0
            break
      else:
        self.x += amt

    def move_y(self, amt):
      if self.solids:
        step = g.sign(amt)
        for i in range(abs(amt) + 1):
          if not self.is_solid(0, step):
            self.y += step
          else:
            self.spd.y = 0
            self.rem.y = 0
            break
      else:
        self.y += amt

    def __str__(self):
      return f'[{self.__class__.__name__}] x: {self.x}, y: {self.y}, rem: {{{self.rem.x:.4f}, {self.rem.y:.4f}}}, spd: {{{self.spd.x:.4f}, {self.spd.y:.4f}}}'

  # objects

  class player_spawn(base_obj):
    def init(self):
      self.target = self.y
      self.y = 128
      self.spd.y = -4
      self.state = 0
      self.delay = 0

    def update(self):
      # jumping up
      if self.state == 0:
        if self.y < self.target + 16:
          self.state = 1
          self.delay = 3
      # falling
      elif self.state == 1:
        self.spd.y += 0.5
        if self.spd.y > 0:
          if self.delay > 0:
            # stall at peak
            self.spd.y = 0
            self.delay -= 1
          elif self.y > self.target:
            # clamp at target y
            self.y = self.target
            self.spd = Vector(0.0, 0.0)
            self.state = 2
            self.delay = 5
      # landing and spawning player object
      elif self.state == 2:
        self.delay -= 1
        if self.delay < 0:
          g.destroy_object(self)
          g.init_object(g.player, self.x, self.y)

  class player(base_obj):
    def init(self):
      self.p_jump = False
      self.p_dash = False
      self.grace = 0
      self.jbuffer = 0
      self.djump = 1
      self.dash_time = 0
      self.dash_effect_time = 0
      self.dash_target = Vector(0.0, 0.0)
      self.dash_accel = Vector(0.0, 0.0)
      self.hitbox = Rect(1, 3, 6, 5)
      self.solids = True

    def update(self):
      # horizontal input
      h_input = 1 if p8.btn(g.k_right) else -1 if p8.btn(g.k_left) else 0

      # spike collision
      if g.spikes_at(self.x + self.hitbox.x, self.y + self.hitbox.y, self.hitbox.w, self.hitbox.h, self.spd.x, self.spd.y):
        g.kill_player(self)

      # bottom death
      if self.y > 128:
        g.kill_player(self)

      # on ground check
      on_ground = self.is_solid(0, 1)

      # jump and dash inmput
      jump = p8.btn(g.k_jump) and not self.p_jump
      dash = p8.btn(g.k_dash) and not self.p_dash
      self.p_jump = p8.btn(g.k_jump)
      self.p_dash = p8.btn(g.k_dash)

      # jump buffer
      if jump:
        self.jbuffer = 4
      elif self.jbuffer > 0:
        self.jbuffer -= 1

      # grace frames and dash restoration
      if on_ground:
        self.grace = 6
        self.djump = g.max_djump
      elif self.grace > 0:
        self.grace -= 1

      # dash effect timer (for dash-triggered events, e.g., berry blocks)
      self.dash_effect_time -= 1

      if self.dash_time > 0:
        self.dash_time -= 1
        self.spd.x = g.appr(self.spd.x, self.dash_target.x, self.dash_accel.x)
        self.spd.y = g.appr(self.spd.y, self.dash_target.y, self.dash_accel.y)
      else:
        maxrun = 1
        accel = (0.6 if not self.is_ice(0, 1) else 0.05) if on_ground else 0.4
        deccel = 0.15

        # set x speed
        self.spd.x = g.appr(self.spd.x, h_input * maxrun, accel) if abs(self.spd.x) <= 1 else g.appr(self.spd.x, g.sign(self.spd.x) * maxrun, deccel)

        # facing direction
        if self.spd.x != 0:
          self.flip.x = self.spd.x < 0

        # terminal vel + wall sliding
        maxfall = 2 if not (h_input != 0 and self.is_solid(h_input, 0) and not self.is_ice(h_input, 0)) else 0.4

        # apply gravity
        if not on_ground:
          self.spd.y = g.appr(self.spd.y, maxfall, 0.21 if abs(self.spd.y) > 0.15 else 0.105)

        # jump
        if self.jbuffer > 0:
          if self.grace > 0:
            self.jbuffer = 0
            self.grace = 0
            self.spd.y = -2
          else:
            wall_dir = -1 if self.is_solid(-3, 0) else 1 if self.is_solid(3, 0) else 0
            if wall_dir != 0:
              self.jbuffer = 0
              self.spd.y = -2
              self.spd.x = -wall_dir * (maxrun + 1)

        # dash
        d_full = 5
        d_half = 3.5355339059

        if self.djump > 0 and dash:
          self.djump -= 1
          self.dash_time = 4
          g.has_dashed = True
          self.dash_effect_time = 10
          # vertical input
          v_input = -1 if p8.btn(g.k_up) else 1 if p8.btn(g.k_down) else 0
          # calculate dash speeds
          self.spd.x = h_input * (d_full if v_input == 0 else d_half) if h_input != 0 else (0 if v_input != 0 else -1 if self.flip.x else 1)
          self.spd.y = v_input * (d_full if h_input == 0 else d_half) if v_input != 0 else 0
          # effects
          g.freeze = 2
          # dash target speeds and accels
          self.dash_target.x = 2 * g.sign(self.spd.x)
          self.dash_target.y = (2 if self.spd.y >= 0 else 1.5) * g.sign(self.spd.y)
          self.dash_accel.x = 1.5 if self.spd.y == 0 else 1.06066017177
          self.dash_accel.y = 1.5 if self.spd.x == 0 else 1.06066017177

      # exit level off the top
      if self.y < -4:
        g.next_room()

    def draw(self):
      if self.x < -1 or self.x > 121:
        self.x = g.clamp(self.x, -1, 121)
        self.spd.x = 0

  class balloon(base_obj):
    def init(self):
      self.timer = 0
      # [change] remove rng, expand hitbox to cover balloon oscillation cycle
      self.hitbox = Rect(-1, -1 - 2, 10, 10 + 4)

    def update(self):
      if self.spr == 22:
        hit = self.check(g.player, 0, 0)
        if hit and hit.djump < g.max_djump:
          hit.djump = g.max_djump
          self.spr = 0
          self.timer = 60
      elif self.timer > 0:
        self.timer -= 1
      else:
        self.spr = 22

  class platform(base_obj):
    def init(self):
      self.x -= 4
      self.hitbox.w = 16
      self.last = self.x
      self.dir = -1 if self.spr == 11 else 1

    def update(self):
      self.spd.x = self.dir * 0.65
      if self.x < -16:
        self.x = 128
      elif self.x > 128:
        self.x = -16
      if not self.check(g.player, 0, 0):
        hit = self.check(g.player, 0, -1)
        if hit:
          hit.move_x(self.x - self.last, 1)
      self.last = self.x

  class fruit(base_obj):
    def init(self):
      self.start = self.y
      self.off = 0

    def update(self):
      hit = self.check(g.player, 0, 0)
      if hit:
        hit.djump = g.max_djump
        g.destroy_object(self)
      self.off += 1
      self.y = self.start + math.sin(self.off / 40) * 2.5

  class fly_fruit(base_obj):
    def init(self):
      self.fly = False
      self.step = 0.5
      self.solids = False

    def update(self):
      if self.fly:
        self.spd.y = g.appr(self.spd.y, -3.5, 0.25)
        if self.y < -16:
          g.destroy_object(self)
      else:
        if g.has_dashed:
          self.fly = True
        self.step += 0.05
        self.spd.y = math.sin(self.step) * 0.5
      hit = self.check(g.player, 0, 0)
      if hit:
        hit.djump = g.max_djump
        g.destroy_object(self)

  class fake_wall(base_obj):
    def update(self):
      self.hitbox.w = 18
      self.hitbox.h = 18
      hit = self.check(g.player, -1, -1)
      if hit and hit.dash_effect_time > 0:
        hit.spd.x = -g.sign(hit.spd.x) * 1.5
        hit.spd.y = -1.5
        hit.dash_time = -1
        g.init_object(g.fruit, self.x + 4, self.y + 4, 26)
        g.destroy_object(self)
      self.hitbox.w = 16
      self.hitbox.h = 16

  class spring(base_obj):
    def init(self):
      self.hide_in = 0
      self.hide_for = 0

    def update(self):
      if self.hide_for > 0:
        self.hide_for -= 1
        if self.hide_for <= 0:
          self.delay = 0
      elif self.spr == 18:
        hit = self.check(g.player, 0, 0)
        if hit and hit.spd.y >= 0:
          self.spr = 19
          hit.y = self.y - 4
          hit.spd.x *= 0.2
          hit.spd.y = -3
          hit.djump = g.max_djump
          self.delay = 10
          below = self.check(g.fall_floor, 0, 1)
          if below:
            g.break_fall_floor(below)
      elif self.delay > 0:
        self.delay -= 1
        if self.delay <= 0:
          self.spr = 18
      if self.hide_in > 0:
        self.hide_in -= 1
        if self.hide_in <= 0:
          self.hide_for = 60
          self.spr = 0

  class fall_floor(base_obj):
    def init(self):
      self.state = 0

    def update(self):
      if self.state == 0:
        if self.check(g.player, 0, -1) or self.check(g.player, -1, 0) or self.check(g.player, 1, 0):
          g.break_fall_floor(self)
      elif self.state == 1:
        self.delay -= 1
        if self.delay <= 0:
          self.state = 2
          self.delay = 60
          self.collideable = False
          self.spr = 0
      elif self.state == 2:
        self.delay -= 1
        if self.delay <= 0 and not self.check(g.player, 0, 0):
          self.state = 0
          self.collideable = True
          self.spr = 23

  def break_spring(self, obj):
    obj.hide_in = 15

  def break_fall_floor(self, obj):
    if obj.state == 0:
      obj.state = 1
      obj.delay = 15
      hit = obj.check(g.spring, 0, -1)
      if hit:
        g.break_spring(hit)

  class key(base_obj):
    def update(self):
      if self.check(g.player, 0, 0):
        g.destroy_object(self)
        g.has_key = True

  class chest(base_obj):
    def init(self):
      self.x -= 4
      self.timer = 20

    def update(self):
      if g.has_key:
        self.timer -= 1
        if self.timer <= 0:
          f = g.init_object(g.fruit, self.x, self.y - 4, 26)
          # [change] remove rng, expand fruit hitbox to cover all outcomes
          f.hitbox.x -= 1
          f.hitbox.w += 3
          g.destroy_object(self)

  # object handling stuff

  def init_object(self, obj, x, y, tile=None):
    o = obj(x, y, tile)
    self.objects.append(o)
    if callable(getattr(o, 'init', None)):
      o.init()
    return o

  def destroy_object(self, obj):
    # [change] remove from list later so update loop doesn't skip
    self.objects[self.objects.index(obj)] = None

  def kill_player(self, obj):
    self.destroy_object(obj)
    self.restart_room()

  # helper functions

  def get_player(self):
    for o in self.objects:
      if type(o) == self.player_spawn or type(o) == self.player:
        return o

  def clamp(self, val, a, b):
    return max(a, min(b, val))

  def appr(self, val, target, amt):
    return max(val - amt, target) if val > target else min(val + amt, target)

  def sign(self, x):
    return 1 if x > 0 else -1 if x < 0 else 0

  def tile_flag_at(self, x, y, w, h, flag):
    for i in range(max(0, int(x / 8)), int(min(15, (x + w - 1) / 8)) + 1):
      for j in range(max(0, int(y / 8)), int(min(15, (y + h - 1) / 8)) + 1):
        if p8.fget(self.tile_at(i, j), flag):
          return True
    return False

  def tile_at(self, x, y):
    return p8.mget(self.room.x * 16 + x, self.room.y * 16 + y)

  def spikes_at(self, x, y, w, h, spdx, spdy):
    for i in range(max(0, int(x / 8)), int(min(15, (x + w - 1) / 8)) + 1):
      for j in range(max(0, int(y / 8)), int(min(15, (y + h - 1) / 8)) + 1):
        tile = self.tile_at(i, j)
        if (tile == 17 and ((y + h - 1) % 8 >= 6 or y + h == j * 8 + 8) and spdy >= 0) or \
         (tile == 27 and y % 8 <= 2 and spdy <= 0) or \
         (tile == 43 and x % 8 <= 2 and spdx <= 0) or \
         (tile == 59 and ((x + w - 1) % 8 >= 6 or x + w == i * 8 + 8) and spdx >= 0):
          return True
    return False

